package JAVA;

public class ArguementsDemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=4;
		int b=5;
		change(a,b);
		System.out.println("a="+a);
		System.out.println("b="+b);
	}
	public static void change(int x,int y)
	{
		x=400;
		y=500;
	}

}
